import java.io.IOException;
import java.nio.file.Files;
import java.nio.file.Paths;
import java.util.List;
List<String> alienList;
try{
alienList=Files.readAllLines(Paths.get("c:/projekty/ufo/aliensToInsert.txt"));
for(String alienLine:alienList){
	String [] tab=alienLine.split(",");
     race=tab[0];
     rank=tab[1];
     testRunner.testCase.testSuite.testCases["AddingAlien"].setPropertyValue("race",race);
     testRunner.testCase.testSuite.testCases["AddingAlien"].setPropertyValue("rank",rank);
     def tCase = testRunner.testCase.testSuite.testCases["AddingAlien"];
     runner = tCase.run(new com.eviware.soapui.support.types.StringToObjectMap(), false);
     log.info(race+" : "+rank+" added");
     log.info(runner.getStatus().toString());
}
}
catch (IOException e){
log.info("Exception");
}

